+
+*{
+  scroll-behavior: smooth;
+}
+body {
+  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto,
+    Oxygen, Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
+    background: #F6F6F5;
+    scroll-behavior: smooth;
+}
+input,select{
+  outline: none;
+}
+.batch_heart{
+  background: tomato;
+  width: 15px;
+  height: 15px;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  border-radius: 100%;
+  position: absolute;
+  right: -24%;
+  top: -9%;
+  cursor: pointer;
+}
+/* Chrome, Safari, Edge, Opera */
+input::-webkit-outer-spin-button,
+input::-webkit-inner-spin-button {
+  -webkit-appearance: none;
+  margin: 0;
+}
+.slider {
+  height: 100vh;
+  position: relative;
+  overflow: hidden;
+  z-index: 1; /* Or higher, as needed */
+}
+
+.slide img {
+  width: 100%;
+  height: auto;
+}
+
+.slide {
+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  background-size: cover;
+  background-position: top center;
+  transition: opacity 1s ease;
+  opacity: 0;
+  background-size: contain;
+  background-repeat: no-repeat;
+}
+
+/* This targets the specific slide, you might need to adjust the selector based on your actual DOM structure */
+.slide, .slide::after, .slide::before, .slider, .slider::after, .slider::before {
+  border: none !important;
+  outline: none !important;
+  box-shadow: none !important;
+}
+
+
+.slide.active {
+  opacity: 1;
+}
+
+.slider-controls {
+  position: absolute;
+  bottom: 50px;
+  left: 100%;
+  transform: translateX(-50%);
+  display: flex;
+}
+
+.slider-indicator {
+  border: none;
+  margin: 0 5px;
+  width: 10px;
+  height: 10px;
+  background-color: #fff;
+  background-size: cover;
+  border-radius: 50%;
+  cursor: pointer;
+  opacity: 0.5;
+  transition: opacity 0.3s ease;
+}
+
+.slider-indicator.active {
+  opacity: 1;
+}
+
+.slider-content {
+  position: absolute;
+  top: 50%;
+  left: 50%;
+  transform: translate(-50%, -50%);
+  z-index: 2; /* Ensure the content is above the slides /
+  text-align: center; / Center the text content */
+  }
+/* Firefox */
+input[type=number] {
+  -moz-appearance: textfield;
+}
